import styled from 'styled-components'

import { conectaApi } from "./conectaApi.js"

const lista = document.querySelector('.lista')

function constroiCard(imagem, titulo, autor, editora, preco){
    const livros = document.createElement('li')
    livros.innerHTML = `
    <div>
        <div>
            <img src=${imagem} alt="capa do livro">
        <div/>
        <div>
            <h1>
                ${titulo}
            </h1>
                <p>${autor}</p>
            <h2>
                ${preco}
            </h2>
                <p>${editora}</p>
        </div>
    </div>`
    
    return livros
}

async function listaLivros(){
    const listaApi = await conectaApi.listaLivros()
    listaApi.forEach(elemento => lista.appendChild(constroiCard(elemento.imagem, elemento.titulo, elemento.autor, elemento.editora, elemento.preco )));
}

listaLivros()

const MainContainer = styled.main`
    display: flex;
    justify-content: center;
    margin = 30%;

`

function Main() {
    return (
      <MainContainer>
        <ul className='lista'></ul>
      </MainContainer>
    )
}

export default Main
