import './estilo.css'
import styled from 'styled-components'

const Caixas = styled.div`
.caixas__cabecalho{
    display: flex;
    align-items: center;
    justify-content: center;
}

.botao__pesquisa{
    width: 40px;
    height: 40px;
    background-image: url('../../imagens/lupa.png');
    background-repeat: no-repeat;
    background-position: center;
    border: none;
    background-color: #00000000;
    cursor: pointer;
}

.barra__pesquisa{
    border-radius: 40px;
    border: none;
    background-color: #ffffffab;
    width: 280px;
    height: 34px;
    padding: 6px;
}

.barra__pesquisa:focus{
    outline: none;
}
`


function CaixaBusca(){
    return(
        <c>
            <input type="search" placeholder='Títulos, Autores, Gêneros e Editoras' className='barra__pesquisa'></input>
            <input type="button" className='botao__pesquisa'></input>
      </c>
    )
}

export default CaixaBusca
