import styled from 'styled-components'
import lupa from '../../imagens/lupa.png'
import { livro } from '../CardLivros/dados'
import { useState } from 'react'

const Caixas = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;    
`
    
const CaixaPesquisa = styled.input`
    border-radius: 40px;
    border: none;
    background-color: #ffffffab;
    width: 280px;
    height: 34px;
    padding: 6px;
    
    &:focus{
        outline: none;
        cursor: text;
    }
`

const BotaoPesquisa = styled.input`
    width: 40px;
    height: 40px;
    background-image: url('${lupa}');
    background-repeat: no-repeat;
    background-position: center;
    border: none;
    background-color: #00000000;
    cursor: pointer;
`


function CaixaBusca(){
    const [ livrosPesquisados, setLivrosPesquisados] = useState([])
    console.log(livrosPesquisados)

    return(
        <Caixas>
            <CaixaPesquisa type="search" placeholder='Títulos, Autores, Gêneros e Editoras' onBlur={evento=>{
                const textoDigitado = evento.target.value
                const resultadoPesquisa = livro.filter(livro => livro.includes(textoDigitado))
                setLivrosPesquisados(resultadoPesquisa)
            }}/>
            {/* <BotaoPesquisa type="button" onClick={()=> {
            }}/> */}
        </Caixas>
    )
}

export default CaixaBusca
