import styled from 'styled-components'
import lupa from '../../imagens/lupa.png'

const Caixas = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;

    input[type=button]{
        width: 40px;
        height: 40px;
        background-image: url('${lupa}');
        background-repeat: no-repeat;
        background-position: center;
        border: none;
        background-color: #00000000;
        cursor: pointer;
    }
    
    
    input[type=search]:focus{
        outline: none;
    }
    `
    
    const CaixaPesquisa = styled.input[type=search]`
        border-radius: 40px;
        border: none;
        background-color: #ffffffab;
        width: 280px;
        height: 34px;
        padding: 6px;

`


function CaixaBusca(){
    return(
        <Caixas>
            <CaixaPesquisa placeholder='Títulos, Autores, Gêneros e Editoras'></CaixaPesquisa>
            <input type="button"></input>
      </Caixas>
    )
}

export default CaixaBusca
