import './estilo.css'
import styled from 'styled-components'

const Caixas = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;

    .botao__pesquisa{
        width: 40px;
        height: 40px;
        background-image: url('../../imagens/lupa.png');
        background-repeat: no-repeat;
        background-position: center;
        border: none;
        background-color: #00000000;
        cursor: pointer;
    }

    input[type=search]{
        border-radius: 40px;
        border: none;
        background-color: #ffffffab;
        width: 280px;
        height: 34px;
        padding: 6px;
    }

    .input[type=search]:focus{
        outline: none;
    }
`


function CaixaBusca(){
    return(
        <Caixas>
            <input type="search" placeholder='Títulos, Autores, Gêneros e Editoras' className='barra__pesquisa'></input>
            <input type="button" className='botao__pesquisa'></input>
      </Caixas>
    )
}

export default CaixaBusca
