import styled from 'styled-components'
import { livro } from '../CardLivros/dados'
import lupa from '../../imagens/lupa.png'
import { useState } from 'react'

const Caixas = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;    
`
    
const CaixaPesquisa = styled.input`
    border-radius: 40px;
    border: none;
    background-color: #ffffffab;
    width: 280px;
    height: 34px;
    padding: 6px;
    
    &:focus{
        outline: none;
        cursor: text;
    }
`

const BotaoPesquisa = styled.input`
    width: 40px;
    height: 40px;
    background-image: url('${lupa}');
    background-repeat: no-repeat;
    background-position: center;
    border: none;
    background-color: #00000000;
    cursor: pointer;
`



function CaixaBusca() {
    const [textoDigitado, setTextoDigitado] = useState('')

    const handlePesquisa = () => {
        const textoDigitadoLowerCase = textoDigitado.toLowerCase()
        const resultadoPesquisa = livro.filter(livro =>
            livro.titulo.toLowerCase().includes(textoDigitadoLowerCase) ||
            livro.autor.toLowerCase().includes(textoDigitadoLowerCase) ||
            livro.editora.toLowerCase().includes(textoDigitadoLowerCase)
        )
        setLivrosPesquisados(resultadoPesquisa);
    }

    return(
        <div>
            <CaixaPesquisa
                type="search"
                placeholder='Títulos, Autores, Gêneros e Editoras'
                onChange={(evento) => setTextoDigitado(evento.target.value)}
            />
            <BotaoPesquisa type="button" onClick={handlePesquisa}/>
        </div>
    )
}

Pesquisa()
// onClick={()=> {}}

export default CaixaBusca
