import styled from 'styled-components'
import { conectaApi } from "../../conectaApi"

const lista = document.querySelector('.lista')


async function listaLivros(){
    const listaApi = await conectaApi.listaLivros()
    listaApi.forEach(elemento => lista.appendChild(constroiCard(elemento.imagem, elemento.titulo, elemento.autor, elemento.editora, elemento.preco )));
}

function constroiCard(imagem, titulo, autor, editora, preco){
    return(
    <div>
        <div>
            <img src={imagem} alt="capa do livro"></img>
        </div>
        <div>
            <h1>
                {titulo}
            </h1>
                <p>{autor}</p>
            <h2>
                {preco}
            </h2>
                <p>{editora}</p>
        </div>
    </div>
    )
}
listaLivros()

const MainContainer = styled.main`
    display: flex;
    justify-content: center;
    margin = 30%;

`

function Main() {
    return (
      <MainContainer>
        <ul>
           
        </ul>
      </MainContainer>
    )
}

export default Main
